    public function selectOrdenes($data, $limit=TRUE){
        $condicion="WHERE (`referencia` like '%" . $data['descripcion'] . "%' OR `asunto` like '%" . $data['descripcion'] . "%' OR `descripcion` like '%" . $data['descripcion'] . "%' OR CONCAT(YEAR(`fecha_emision`),LPAD(`id_orden`, 5, '0')) like '%" . $data['descripcion'] . "%')";
        if (Session::get('role') == 3) {
            $data['responsable']=Session::get('iduser');
        }
        if($data['responsable'] !="__jcombo__" and $data['responsable']!=""){
            $condicion.=" AND orden.id_usuario_recibe=".$data['responsable'];
        }

        if($data['pendiente']!="" and $data['enproceso'] !="" and $data['ejecutada'] !=""){
            $condicion.=" and (estatus=".$data['pendiente']." or estatus=".$data['enproceso']." or estatus=".$data['ejecutada'].')';
        }elseif($data['pendiente']!="" or $data['enproceso']!="" or $data['ejecutada']!=""){
            $boll=FALSE;
            $condicion.=" and ";
            if($data['pendiente']!=""){
                $estatus="estatus=".$data['pendiente'];
                $boll=TRUE;
            }
            if($data['enproceso']!=""){
                if(!$boll){
                    $estatus=" estatus=".$data['enproceso'];
                    $boll=TRUE;
                }else{
                    $estatus.=" or estatus=".$data['enproceso'];
                }

            }

            if($data['ejecutada']!=""){
                if(!$boll){
                    $estatus=" estatus=".$data['ejecutada'];
                    $boll=TRUE;
                }else{
                    $estatus.=" or estatus=".$data['ejecutada'];
                }
            }
            $condicion.='('.$estatus.')';
        }

        if(isset($data['gobcalle']) and $data['gobcalle']==1){
            $condicion.=" AND gc=1";
        }
        $data['fechadesde']=str_replace('-','/', $data['fechadesde']);
        $data['fechahasta']=str_replace('-','/', $data['fechahasta']);
        $fechadesde = date_parse($data['fechadesde']);
        $fechahasta = date_parse($data['fechahasta']);
        $checkfechadesde=checkdate($fechadesde["month"], $fechadesde["day"], $fechadesde["year"]);
        //exit(print_r($fechadesde));
        $checkfechahasta=checkdate($fechahasta["month"], $fechahasta["day"], $fechahasta["year"]);
        if ($checkfechadesde AND $checkfechahasta){
            $condicion.=" AND DATE(`fecha_emision`) >= '" . $data['fechadesde'] . "' AND DATE(`fecha_emision`) <= '" . $data['fechahasta'] . "'";
        }elseif($checkfechadesde){
            $condicion.=" AND DATE(`fecha_emision`) >= '" . $data['fechadesde'] . "'";
        }elseif($checkfechahasta){
            $condicion.=" AND DATE(`fecha_emision`) <= '" . $data['fechahasta'] . "'";
        }
        //exit($condicion);
        //exit("SELECT `orden`.`id_orden`, descripcion,date_format(fecha_emision,'%d/%m/%Y') as fecha_emision,date_format(DATE_ADD(fecha_emision,INTERVAL plazo DAY),'%d/%m/%Y') as fecha_culminacion, DATEDIFF(NOW(),DATE_ADD(fecha_emision,INTERVAL plazo DAY)) as dias_restantes,estatus, `app_user`.`nombres` as 'usuario_nombre', `institutos`.`nombre` as 'usuario_instituto' FROM orden LEFT JOIN app_user ON `app_user`.`iduser`=`orden`.`id_usuario_recibe` LEFT JOIN `institutos` ON `app_user`.`id_instituto`=`institutos`.`id` " . $condicion . " ORDER BY `orden`.`id_orden` DESC LIMIT " . 10*($data['pagina']-1) . " , " . $data['cantidad']);
        if ($limit==TRUE) {
            $limit = "LIMIT " . 10*($data['pagina']-1) . " , " . $data['cantidad'];
        }else{
            $limit = "";
        }
        $data=$this->db->select("SELECT CONCAT(YEAR(`fecha_emision`),LPAD(`id_orden`, 5, '0')) as 'codigo',`orden`.`id_orden`, descripcion,date_format(fecha_emision,'%d/%m/%Y') as fecha_emision,date_format(DATE_ADD(fecha_emision,INTERVAL plazo DAY),'%d/%m/%Y') as fecha_culminacion, DATEDIFF(NOW(),DATE_ADD(fecha_emision,INTERVAL plazo DAY)) as dias_restantes,estatus, `app_user`.`nombres` as 'usuario_nombre', `institutos`.`nombre` as 'usuario_instituto' FROM orden LEFT JOIN app_user ON `app_user`.`iduser`=`orden`.`id_usuario_recibe` LEFT JOIN `institutos` ON `app_user`.`id_instituto`=`institutos`.`id` " . $condicion . " ORDER BY `orden`.`id_orden` DESC ".$limit);
        $count=$this->db->select("SELECT count(`orden`.`id_orden`) as 'cantidad_orden' FROM orden LEFT JOIN app_user ON `app_user`.`iduser`=`orden`.`id_usuario_recibe` LEFT JOIN `institutos` ON `app_user`.`id_instituto`=`institutos`.`id` " . $condicion . " ORDER BY `orden`.`id_orden` DESC");
        $total=array('d'=>$data, 'i'=>$count[0]['cantidad_orden']);
        return $total;
    }

    public function buscarOrden($id){
        return $this->db->select("SELECT `orden`.`id_orden`, `orden`.`referencia`, `orden`.`asunto`, `orden`.`plazo`, `orden`.`medio`, `orden`.`oficio`, `orden`.`fecha_emision`, descripcion,date_format(fecha_emision,'%d/%m/%Y') as fecha_emision,date_format(DATE_ADD(fecha_emision,INTERVAL plazo DAY),'%d/%m/%Y') as fecha_culminacion, DATEDIFF(NOW(),DATE_ADD(fecha_emision,INTERVAL plazo DAY)) as dias_restantes,estatus, `app_user`.`nombres` as 'usuario_nombre', `app_user`.`iduser` as 'iduser', `institutos`.`nombre` as 'usuario_instituto' FROM orden LEFT JOIN app_user ON `app_user`.`iduser`=`orden`.`id_usuario_recibe` LEFT JOIN `institutos` ON `app_user`.`id_instituto`=`institutos`.`id` WHERE `orden`.`id_orden`=:idorden", array(':idorden'=>$id));
    }
    public function cantidadordenes(){
        if (Session::get('role') == 3) {
            $where="WHERE `app_user`.`iduser`=" . Session::get('iduser');
        }else{
            $where="";
        }
        return $this->db->select("SELECT `institutos`.`nombre` as 'instituto', `app_user`.*, count(if(`orden`.`estatus` = 0, `orden`.`estatus`, NULL)) as pendiente_orden, count(if(`orden`.`estatus` = 1, `orden`.`estatus`, NULL)) as proceso_orden, count(if(`orden`.`estatus` = 2, `orden`.`estatus`, NULL)) as ejecutada_orden, count(`orden`.`id_usuario_recibe`) as total_orden FROM `app_user` LEFT JOIN `institutos` ON `app_user`.`id_instituto`=`institutos`.`id` LEFT JOIN  `orden` ON `app_user`.`iduser` =`orden`.`id_usuario_recibe` $where GROUP BY `app_user`.`iduser` HAVING total_orden>0 ORDER BY total_orden DESC");
    }
