    public function cambiarestatus($idOrden,$estatus) {
        $postData = array(
            'estatus' => $estatus
        );
        switch ($estatus) {
            case 0:
                $name="Pendiente";
                $icon="icon-star-empty";
                $nextstyle="btn-primary";
                $nextname="En Proceso";
                $nextid="1";
                $nexticon="icon-star-half-empty";
                break;
            case 1:
                $name="En Proceso";
                $icon="icon-star-half-empty";
                $nextstyle="btn-warning";
                $nextname="Ejecutado";
                $nextid="2";
                $nexticon="icon-star";
                break;
            case 2:
                $name="Ejecutado";
                $icon="icon-star";
                $nextstyle="";
                $nextname="";
                $nextid="";
                $nexticon="";
                break;
            default:
                $name="Pendiente";
                $icon="icon-star-empty";
                $nextname="En Proceso";
                $nextid="1";
                $nexticon="icon-star-half-empty";
                break;
        }
        $newDiv='<div class="btn disabled"><i class="' .$icon. '"></i> <strong>' .$name. '</strong></div>';
        if($nextname!=''){
            $newDiv.=', Campiar a: <button class="btn ' .$nextstyle. '" onclick="cambiarEstatus(' .$idOrden. ',' .$nextid. ')" type="button"><i class="' .$nexticon. '"></i> ' .$nextname. '</button>';
        }
        if($this->db->update(' orden', $postData, "`id_orden` = {$idOrden}")){
            return array("mensaje" => 'Estatus Cambiado Exitosamente', 'newDiv' => $newDiv);
        }
    }

